//the default user to update and delete is {user_Id:1,username:'testuser',email:'test@test.com',password:'password}
import "./App.css";
import { useEffect, useState } from "react";
import AllRecipes from "./components/allRecipes.jsx";
import OneRecipe from "./components/OneRecipe.jsx";
import Home from "./components/Home.jsx";
import Add from "./components/Add.jsx";
import dummyData from "./data/data.json";
import axios from "axios";
function App() {
  const [view, setView] = useState("Home");
  const [data, setData] = useState(dummyData);
  const [one, setOne] = useState(data[0]);
  const [search, setSearch] = useState("");
  useEffect(() => {
    axios.get("http://localhost:4000/recipe").then((res) => {
      setData(res.data);
    });
  }, []);
  let changeView = (view) => {
    setView(view);
  };
  return (
    <div className="App">
      <nav className="nav">
        <div
          className="nav-item is-active"
          active-color="orange"
          onClick={() => setView("Home")}
        >
          Home
        </div>

        <div
          className="nav-item"
          active-color="green"
          
          onClick={() => setView("AllRecipes")}
        >
          All Recipies
        </div>
        <div
          className="nav-item"
          active-color="red"
          onClick={() => setView("AddRecipe")}
        >
          AddRecipe
        </div>
        <div className="nav-item" active-color="red">
          <input type="text" onChange={(e) => setSearch(e.target.value)} />
          <button
            onClick={() => {
              setData(
                data.filter((e) =>
                  e.Recipe_Name.toLowerCase().includes(search.toLowerCase())
                )
              );
            }}
          >
            search
          </button>
        </div>
        <span className="nav-indicator"></span>
      </nav>
      {view === "Home" && <Home changeView={changeView} />}
      {view === "AllRecipes" && (
        <AllRecipes changeView={changeView} setOne={setOne} data={data} />
      )}
      {view === "AddRecipe" && <Add one={one} />}
      {view === "OneRecipe" && <OneRecipe one={one} />}
      <div></div>
    </div>
  );
}

export default App;
